    <span class="icon" id="icon_wechat1"></span>
    <span class="box">
        <a class="name">萌猴截图</a><br>
    </span>
    <a class="btn" href="itms-services://?action=download-manifest&url=https://dafuai.com/k/cf867d914213dfc29eb863b87c9f44afb336aa">点击安装</a>
</div>
<br><br><br><br><br><br>

<div class="other">
    <div class="search">
        <input class="search-input" type="text" placeholder="输入激活码" id="code">
    </div>

    <meta name="_token" content="{{ csrf_token() }}"/>
    <a class="btn" onclick="login();" style="background-color:#00b934;text-align:center;" href="javascript:;">
        查询状态
    </a>
</div>

<script type='text/javascript' src='//g.alicdn.com/sj/lib/zepto/zepto.min.js' charset='utf-8'></script>
<script type='text/javascript' src='//g.alicdn.com/msui/sm/0.6.2/js/sm.min.js' charset='utf-8'></script>

<script type="text/javascript">
    var url1 = "https://www.wsklx.com/mhuser/queryResult";
    function login(){
        var code = document.getElementById("code").value;
        if (!code) {
            show("提示信息","激活码不能为空");
        } else {
            if (url1 == '') {
                show("查询结果","激活码"+ code +"不存在");
                return;
            }
            var paramUrl = '?queryActivate='+code;
            url = url1+paramUrl;
            $.ajax({
                url:url,
                processData: false,
                type:'get',
                dataType:'json',
                success:function(data){
                    show("提示", data.message);
                },
                error:function(XMLHttpRequest, textStatus, errorThrown) {
                    alert(XMLHttpRequest.readyState);
                    alert(textStatus);
                    alert(errorThrown);
                    show("查询结果","网络错误");
                }
            });
        }
    }

    function show(titles, texts){
        $.modal({
            title:  titles,
            text: texts,
            buttons: [
                {
                    text: '我知道了',
                    bold: true
                },
            ]
        })
    }
</script>
</body>
